    if NODE_ID_PATH.endswith(".npy"):
        all_node_ids = np.load(NODE_ID_PATH).astype(int)
    elif NODE_ID_PATH.endswith(".txt"):
        all_node_ids = np.loadtxt(NODE_ID_PATH, dtype=int)
